HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
};

class DesktopContainer extends Component {
  state = {};

  hideFixedMenu = () => this.setState({ fixed: false });
  showFixedMenu = () => this.setState({ fixed: true });

  render() {
    const { children } = this.props;
    const { fixed } = this.state;

    return (
      <Media greaterThan="mobile">
        <div
          style={{
            background: `url(/images/wp6058480.jpg)`,
            backgroundSize: "cover",
          }}
        >
          <Visibility
            once={false}
            onBottomPassed={this.showFixedMenu}
            onBottomPassedReverse={this.hideFixedMenu}
          >
            <Segment textAlign="center" className={`bg-cover p-1-0`} vertical>
              <Menu
                fixed={fixed && "top"}
                inverted={!fixed}
                pointing={!fixed}
                size="small"
                className={fixed ? "bg-linear" : "bg-unset"}
                borderless
              >
                <Container style={{ alignItems: "center" }}>
                  <Menu.Item style={{ padding: "3px 0" }}>
                    <Image size="tiny" src="/images/tachyon-purple.png" />
                  </Menu.Item>
                  <Link href="/">
                    <Menu.Item as="a" className="navbar-item menu-item">
                      Home
                    </Menu.Item>
                  </Link>
                  <Link href="/about-me">
                    <Menu.Item as="a" className="navbar-item menu-item">
                      About Me
                    </Menu.Item>
                  </Link>
                  <Menu.Item as="a" className="navbar-item menu-item">
                    Categories
                  </Menu.Item>
                  <Menu.Item as="a" className="navbar-item menu-item">
                    Careers
                  </Menu.Item>
                  <Menu.Item position="right">
                    <Button
                      className="cool-button"
                      as="a"
                      style={{ marginLeft: "0.5em", fontSize: "1.2em" }}
                    >
                      Sign in
                    </Button>
                    <Button
                      className="cool-button"
                      as="a"
                      style={{ marginLeft: "0.5em", fontSize: "1.2em" }}
                    >
                      Sign Up
                    </Button>
                  </Menu.Item>
                </Container>
              </Menu>
              {/* <img
              className="star-1-drop"
              src="images/stars.png"
              alt="star drop"
            /> */}
              {/* <HomepageHeading /> */}
            </Segment>
          </Visibility>
          <Container>{children}</Container>
        </div>
        <Footer />
      </Media>
    );
  }
}

DesktopContainer.propTypes = {
  children: PropTypes.node,
};

class MobileContainer extends Component {
  state = {};

  handleSidebarHide = () => this.setState({ sidebarOpened: false });

  handleToggle = () => this.setState({ sidebarOpened: true });

  render() {
    const { children } = this.props;
    const { sidebarOpened } = this.state;

    return (
      <Media as={Sidebar.Pushable} at="mobile">
        <Sidebar.Pushable>
          <Sidebar
            as={Menu}
            animation="overlay"
            icon="labeled"
            onHide={this.handleSidebarHide}
            vertical
            visible={sidebarOpened}
            className="bg-linear"
          >
            <Menu.Item>
              <Image
                size="tiny"
                src="/images/tachyon-purple.png"
                style={{ display: "inline-block" }}
              />
            </Menu.Item>
            <Menu.Item as="a" className="navbar-item">
              Home
            </Menu.Item>
            <Menu.Item as="a" className="navbar-item">
              Work
            </Menu.Item>
            <Menu.Item as="a" className="navbar-item">
              Company
            </Menu.Item>
            <Menu.Item as="a" className="navbar-item">
              Careers
            </Menu.Item>
          </Sidebar>

          <Sidebar.Pusher dimmed={sidebarOpened}>
            <Segment
              inverted
              textAlign="center"
              className="bg-cover"
              // style={{
              //   minHeight: 700,
              //   padding: "1em 0em",
              // }}
              vertical
            >
              <Container>
                <Menu inverted borderless secondary size="large">
                  <Menu.Item onClick={this.handleToggle}>
                    <Icon name="sidebar" style={{ color: "#d400da" }} />
                  </Menu.Item>
                  <Menu.Item position="right">
                    <Button
                      className="cool-button"
                      as="a"
                      style={{ fontSize: "1.2em" }}
                    >
                      Sign in
                    </Button>
                    <Button
                      as="a"
                      className="cool-button"
                      style={{ marginLeft: "0.5em", fontSize: "1.2em" }}
                    >
                      Sign Up
                    </Button>
                  </Menu.Item>
                </Menu>
              </Container>
              {/* <HomepageHeading mobile /> */}
            </Segment>
            {children}
          </Sidebar.Pusher>
        </Sidebar.Pushable>
        <Footer />
      </Media>
    );
  }
}

MobileContainer.propTypes = {
  children: PropTypes.node,
};

export const ResponsiveContainer = ({ children }) => (
  /* Heads up!
   * For large applications it may not be best option to put all page into these containers at
   * they will be rendered twice for SSR.
   */
  <MediaContextProvider>
    <DesktopContainer>{children}</DesktopContainer>
    <MobileContainer>{children}</MobileContainer>
  </MediaContextProvider>
);

ResponsiveContainer.propTypes = {
  children: PropTypes.node,
};
